var inputValue = document.getElementById("bug_type").value

if (inputValue == 'feature') {

   var feature = ['new', 'started', 'completed'];
   dropdown_two.options.length = 0;
   for (i = 0; i < feature.length; i++) {
              createOptions(dropdown_two, feature[i], feature[i]);
            }
} else{

    var bug = ['new', 'started', 'resolved'];
   dropdown_two.options.length = 0;
   for (i = 0; i < bug.length; i++) {
              createOptions(dropdown_two, bug[i], bug[i]);
            }
}

 function createOptions(dropdown_two, text, value) {
        var opt = document.createElement('option');
        opt.value = value;
        opt.text = text;
          dropdown_two.options.add(opt);
      }
