var inputValue = document.getElementById("bug_bug_type").value

if (inputValue == 'feature') {

   var feature = ['new', 'started', 'completed'];
   status.options.length = 0;
   for (i = 0; i < feature.length; i++) {
              createOptions(status, feature[i], feature[i]);
            }
} else{

    var bug = ['new', 'started', 'resolved'];
   status.options.length = 0;
   for (i = 0; i < bug.length; i++) {
              createOptions(status, bug[i], bug[i]);
            }
}

 function createOptions(status, text, value) {
        var opt = document.createElement('option');
        opt.value = value;
        opt.text = text;
          status.options.add(opt);
      }
